/* eslint-disable global-require */

import * as WebBrowser from "expo-web-browser";
import React, { Component } from "react";
import * as Permissions from 'expo-permissions';
import * as Location from 'expo-location';
import * as TaskManager from 'expo-task-manager';
import Constants from 'expo-constants';

import {
  Platform,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from "react-native";

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    paddingTop: Constants.statusBarHeight,
    backgroundColor: '#ecf0f1',
  },
  welcome: {
    fontSize: 20,
    textAlign: 'center',
    margin: 10,
  },
  instructions: {
    textAlign: 'center',
    color: '#333333',
    marginBottom: 5,
  },
  paragraph: {
    margin: 24,
    fontSize: 18,
    textAlign: 'center',
    color: '#000',
  },
});

const TASK_GUARDIAN_LOCATION = "guardian_location"

TaskManager.defineTask(TASK_GUARDIAN_LOCATION, ({data, error}) => {
  if (error) {
    return;
  }
  if (data) {
    const { locations } = data;
    // TODO: AsyncStorage
    // TODO: SecureStorage
  }

})


// TODO: convert to functional component!
class LocationScreen extends Component {

  state = {
    location: null,
    errorMessage: null,
  };

  constructor(props) {
    super(props);
    if (Platform.OS === 'android' && !Constants.isDevice) {
      this.setState({
        errorMessage: 'Oops, this will not work on Sketch in an Android emulator. Try it on your device!',
      });
    } else {
      this.watchLocation();
    }
  }

  componentDidMount() {
    this.watchLocation();
  }

  watchLocation = async () => {
    let location = await Location.getCurrentPositionAsync({});
    this.setState({ location });
  }

  onPress = async () => {
    const { status, ios } = await Location.requestPermissionsAsync();
    if (status !== 'granted') {
      this.setState({
        errorMessage: 'Permission to access location was denied',
      });
    }
    if (status === 'granted') {
      await Location.startLocationUpdatesAsync(
        TASK_GUARDIAN_LOCATION, {
          accuracy: Location.HIGH,
          showsBackgroundLocationIndicator: true,
          //timeInterval: 0,
          distanceInterval: '20', // meters
          deferredUpdatesInterval: '200', //ms
          deferredUpdatesDistance: '20', //meters
          pausesUpdatesAutomatically: true,
      })
    }
  };

  render() {
    let text = 'Waiting..';
    if (this.state.errorMessage) {
      text = this.state.errorMessage;
    } else if (this.state.location) {
      text = JSON.stringify(this.state.location);
    }

    return (
      <View style={styles.container}>
        <TouchableOpacity onPress={this.onPress}>
          <Text>Enable background location</Text>
        </TouchableOpacity>
        <Text style={styles.paragraph}>{text}</Text>
      </View>
    );
  }
};

LocationScreen.navigationOptions = {
  header: null,
};

export default LocationScreen;

/* eslint-enable global-require */
